pageContext.rootQuery:
query blogQuery($lang: String!) {
  prismic {
    allBlogPages(lang: $lang) {
      edges {
        node {
          bgColor
          bgImage
          pageTitle
          pageDescription
          text
          title
          pageKeywords {
            keyword
          }
          _meta {
            lang
            uid
            type
          }
          body {
            ... on PRISMIC_BlogPageBodyTestimonials {
              type
              label
              primary {
                anchor
                text
                title
                bgColor
                bgImage
              }
              fields {
                clientName
                clientPhoto
                image
                link {
                  ...link
                }
                linkText
                linkStyle
                rating
                text
                videoUrl
              }
            }
            ... on PRISMIC_BlogPageBodyGallery {
              type
              label
              primary {
                anchor
                bgColor
                bgImage
                text
                title
              }
              fields {
                image
                text
                title
              }
            }
            ... on PRISMIC_BlogPageBodyPricingPlans {
              type
              label
              primary {
                anchor
                bgColor
                bgImage
                title
                text
              }
              fields {
                priceUnits
                planPrice
                planName
                planDetails
                linkText
                linkStyle
                link {
                  ...link
                }
                isFreePlan
              }
              primary {
                bgImage
                bgColor
                title
                text
              }
            }
            ... on PRISMIC_BlogPageBodyItemsCollection {
              label
              type
              primary {
                anchor
                bgColor
                bgImage
                text
                title
                linkStyle
                linkText
              }
              fields {
                tag
              }
            }
            ... on PRISMIC_BlogPageBodyText {
              type
              label
              primary {
                anchor
                title
                text
                bgColor
                bgImage
              }
            }
            ... on PRISMIC_BlogPageBodyFeature {
              type
              label
              primary {
                anchor
                bgColor
                bgImage
                title
                text
              }
              fields {
                image
                linkStyle
                linkText
                title
                text
                link {
                  ...link
                }
              }
            }
            ... on PRISMIC_BlogPageBodyBlockWithTextAndImage {
              label
              type
              primary {
                anchor
                bgColor
                bgImage
                minHeight
                title
                text
                image
                link {
                  ...link
                }
                linkStyle
                linkText
              }
            }
            ... on PRISMIC_BlogPageBodyForm {
              type
              label
              primary {
                anchor
                bgColor
                bgImage
                formScript
                formUrl
                title
                text
              }
            }
          }
        }
      }
    }
    allBlogCategorys(lang: $lang) {
      edges {
        node {
          title
          _meta {
            uid
            type
            lang
          }
        }
      }
    }
  }
}
