pageContext.rootQuery:
query postQuery($lang: String!, $uid: String!) {
  prismic {
    blogPost(uid: $uid, lang: $lang) {
      _meta {
        type
        uid
        lang
        lastPublicationDate
      }
      title
      text
      image
      isFeaturedArticle
      bgColor
      pageDescription
      pageKeywords {
        keyword
      }
      pagePreviewImage
      pageTitle
      body {
        ... on PRISMIC_BlogPostBodyTestimonials {
          type
          label
          primary {
            anchor
            text
            title
            bgColor
            bgImage
          }
          fields {
            clientName
            clientPhoto
            image
            link {
              ...link
            }
            linkText
            linkStyle
            rating
            text
            videoUrl
          }
        }
        ... on PRISMIC_BlogPostBodyGallery {
          type
          label
          primary {
            anchor
            bgColor
            bgImage
            text
            title
          }
          fields {
            image
            text
            title
          }
        }
        ... on PRISMIC_BlogPostBodyPricingPlans {
          type
          label
          fields {
            priceUnits
            planPrice
            planName
            planDetails
            linkText
            linkStyle
            link {
              ...link
            }
            isFreePlan
          }
          primary {
            anchor
            bgImage
            bgColor
            title
            text
          }
        }
        ... on PRISMIC_BlogPostBodyItemsCollection {
          label
          type
          primary {
            anchor
            bgColor
            bgImage
            text
            title
            linkStyle
            linkText
          }
          fields {
            tag
          }
        }
        ... on PRISMIC_BlogPostBodyText {
          type
          label
          primary {
            anchor
            text
            bgColor
            bgImage
          }
        }
        ... on PRISMIC_BlogPostBodyFeature {
          type
          label
          primary {
            anchor
            bgColor
            bgImage
            text
          }
          fields {
            image
            linkStyle
            linkText
            text
            title
            link {
              ...link
            }
          }
        }
        ... on PRISMIC_BlogPostBodyBlockWithTextAndImage {
          label
          type
          primary {
            anchor
            bgColor
            bgImage
            minHeight
            title
            text
            image
            link {
              ...link
            }
            linkStyle
            linkText
          }
        }
        ... on PRISMIC_BlogPostBodyForm {
          type
          label
          primary {
            anchor
            bgColor
            bgImage
            formScript
            formUrl
            text
          }
        }
        ... on PRISMIC_BlogPostBodyText {
          type
          label
          primary {
            anchor
            title
            text
            bgColor
            bgImage
          }
        }
      }
    }
  }
}
