pageContext.rootQuery:
query blogCategoryQuery($lang: String!, $uid: String!) {
  prismic {
    blogCategory(lang: $lang, uid: $uid) {
      bgColor
      bgImage
      pageTitle
      pageDescription
      text
      title
      pageKeywords {
        keyword
      }
      _meta {
        lang
        uid
        type
      }
      body {
        ... on PRISMIC_BlogCategoryBodyTestimonials {
          type
          label
          primary {
            anchor
            text
            title
            bgColor
            bgImage
          }
          fields {
            clientName
            clientPhoto
            image
            link {
              ...link
            }
            linkText
            linkStyle
            rating
            text
            videoUrl
          }
        }
        ... on PRISMIC_BlogCategoryBodyGallery {
          type
          label
          primary {
            anchor
            bgColor
            bgImage
            text
            title
          }
          fields {
            image
            text
            title
          }
        }
        ... on PRISMIC_BlogCategoryBodyPricingPlans {
          type
          label
          primary {
            anchor
            bgColor
            bgImage
            title
            text
          }
          fields {
            priceUnits
            planPrice
            planName
            planDetails
            linkText
            linkStyle
            link {
              ...link
            }
            isFreePlan
          }
        }
        ... on PRISMIC_BlogCategoryBodyItemsCollection {
          label
          type
          primary {
            anchor
            bgColor
            bgImage
            text
            title
            linkStyle
            linkText
          }
          fields {
            tag
          }
        }
        ... on PRISMIC_BlogCategoryBodyText {
          type
          label
          primary {
            anchor
            title
            text
            bgColor
            bgImage
          }
        }
        ... on PRISMIC_BlogCategoryBodyFeature {
          type
          label
          primary {
            anchor
            bgColor
            bgImage
            title
            text
          }
          fields {
            image
            linkStyle
            linkText
            title
            text
            link {
              ...link
            }
          }
        }
        ... on PRISMIC_BlogCategoryBodyBlockWithTextAndImage {
          label
          type
          primary {
            anchor
            bgColor
            bgImage
            minHeight
            title
            text
            image
            link {
              ...link
            }
            linkStyle
            linkText
          }
        }
        ... on PRISMIC_BlogCategoryBodyForm {
          type
          label
          primary {
            anchor
            bgColor
            bgImage
            formScript
            formUrl
            title
            text
          }
        }
      }
    }
    allBlogCategorys(lang: $lang) {
      edges {
        node {
          title
          _meta {
            uid
            type
            lang
          }
        }
      }
    }
  }
}
